import openSocket from "socket.io-client";
const socket = openSocket("http://localhost:8000");

function subscribeToDrawings(cb){
	socket.on("drawing", cb)
	socket.emit('subscribeToDrawings', 1000);
}

export {
	subscribeToDrawings
};
